ns8:component-version-script.script:
#!/bin/bash export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin #Create database needed for Dukes bank
# duke's bank's db script INIT_DB_SCRIPT_FILE=init_db_script_file.sql echo "create user '${db_username}'@'localhost' identified by '${db_password}';" > $INIT_DB_SCRIPT_FILE echo "create user '${db_username}' identified by '${db_password}';" >> $INIT_DB_SCRIPT_FILE echo "create database ${sample_db};" >> $INIT_DB_SCRIPT_FILE echo "grant all privileges on ${sample_db}.* to '${db_username}'@'localhost' identified by '${db_password}';" >> $INIT_DB_SCRIPT_FILE echo "grant all privileges on ${sample_db}.* to '${db_username}' identified by '${db_password}';" >> $INIT_DB_SCRIPT_FILE echo "flush privileges;" >> $INIT_DB_SCRIPT_FILE
echo "USE ${sample_db};" >> $INIT_DB_SCRIPT_FILE echo "source ${initialize_db_script};" >> $INIT_DB_SCRIPT_FILE mysql -u$init_db_username -p$init_db_password < $INIT_DB_SCRIPT_FILE

#!/bin/bash # Import global conf . $global_conf
export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin set -e
. $jboss_configure_script 2>&1 | tee /tmp/jboss_cfg_output

#!/bin/bash # Import global conf . $global_conf
