ns9:component-version-script.script:
#!/bin/bash

################################################# 
# SCRIPT TO INSTALL THE ORACLE DATABASE SOFTWARE
#################################################

#PARAMETERS FROM APP DIRECTOR 
ORACLE_INVENTORY_LOCATION="$oracle_inventory_location" 
ORACLE_HOME="$oracle_home"
DATA_FILE_DESTINATION="$data_file_destination"  
RECOVERY_AREA_DESTINATION="$recovery_area_destination"
ORACLE_USER="$oracle_user"

#FUNCTION TO DISPLAY ERROR AND EXIT
function check_error()
{
   if [ ! "$?" = "0" ]; then
      error_exit "$1";
   fi
}

function error_exit()
{
   echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2
   exit 1
}

if [ "x${ORACLE_INVENTORY_LOCATION}" = "x" ]; then 
    error_exit "ORACLE_INVENTORY_LOCATION not set."
fi

if [ "x${ORACLE_HOME}" = "x" ]; then 
    error_exit "ORACLE_HOME not set."
fi

#AFTER INSTALLATION OF DATABASE, NEED TO EXECUTE TWO FILES (orainstRoot.sh, root.sh) AS ROOT USER 
$ORACLE_INVENTORY_LOCATION/orainstRoot.sh 
check_error "UNABLE TO EXECUTE orainstRoot.sh SUCCESSFULLY.";

$ORACLE_HOME/root.sh
check_error "UNABLE TO EXECUTE root.sh SUCCESSFULLY.";

#CREATING THE FOLDERS FOR DATABASE
mkdir -p $DATA_FILE_DESTINATION 
mkdir -p $RECOVERY_AREA_DESTINATION
check_error "FAILED TO CREATE DATA_FILE_DESTINATION AND RECOVERY_FILE_DESTINATION FOLDERS.";

#CHANGING THE OWENSHIP OF THE FOLDER
chown -R $ORACLE_USER:oinstall $DATA_FILE_DESTINATION $RECOVERY_AREA_DESTINATION 
check_error "FAILED TO CHANGE OWNERSHIP OF DATA_FILE_DESTINATION AND RECOVERY_FILE_DESTINATION FOLDERS.";
