#!/bin/bash

################################################# 
# SCRIPT TO INSTALL THE ORACLE DATABASE SOFTWARE
#################################################

#PARAMETERS FROM APP DIRECTOR 
ORACLE_INVENTORY_LOCATION="$oracle_inventory_location" 
ORACLE_HOME="$oracle_home"
DATA_FILE_DESTINATION="$data_file_destination"  
RECOVERY_AREA_DESTINATION="$recovery_area_destination"
ORACLE_USER="$oracle_user"

#FUNCTION TO DISPLAY ERROR AND EXIT
function check_error()
{
   if [ ! "$?" = "0" ]; then
      error_exit "$1";
   fi
}

function error_exit()
{
   echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2
   exit 1
}

if [ "x${ORACLE_INVENTORY_LOCATION}" = "x" ]; then 
    error_exit "ORACLE_INVENTORY_LOCATION not set."
fi

if [ "x${ORACLE_HOME}" = "x" ]; then 
    error_exit "ORACLE_HOME not set."
fi

#AFTER INSTALLATION OF DATABASE, NEED TO EXECUTE TWO FILES (orainstRoot.sh, root.sh) AS ROOT USER 
$ORACLE_INVENTORY_LOCATION/orainstRoot.sh 
check_error "UNABLE TO EXECUTE orainstRoot.sh SUCCESSFULLY.";

$ORACLE_HOME/root.sh
check_error "UNABLE TO EXECUTE root.sh SUCCESSFULLY.";

#CREATING THE FOLDERS FOR DATABASE
mkdir -p $DATA_FILE_DESTINATION 
mkdir -p $RECOVERY_AREA_DESTINATION
check_error "FAILED TO CREATE DATA_FILE_DESTINATION AND RECOVERY_FILE_DESTINATION FOLDERS.";

#CHANGING THE OWENSHIP OF THE FOLDER
chown -R $ORACLE_USER:oinstall $DATA_FILE_DESTINATION $RECOVERY_AREA_DESTINATION 
check_error "FAILED TO CHANGE OWNERSHIP OF DATA_FILE_DESTINATION AND RECOVERY_FILE_DESTINATION FOLDERS.";

#!/bin/sh
######################################################################################################################################### # SCRIPT TO DEPLOY SAMPLE APPLICATION WAR. THIS SAMPLE APPLICATION WILL CREATE A TABLE IN THE DB CREATED IN THE ABOVE STAGE # ###### TO DEMONSTRATE ACTIVE-ACTIVE AND ACTIVE-PASSIVE####### # THE SAMPLE APPLICATION READ TWO FILES, ip.txt CONTAINS IP OF RAC MAIN NODE AND ip_other.txt CONTAINS IP OF FIRST NODE OF THE RAC SLAVE # THE ip_other.txt IS USED IN THE SAMPLE APPLICATION ONLY FOR THE MODIFICATION (UPDATEING) VALUES IN THE EXISTING TABLE # THUS UPDATE OPERATION WILL WORK ONLY IN ACTIVE-ACTIVE MODE SINCE IN ACTIVE-PASSIVE MODE ONLY ONE NODE IS ACTIVE #########################################################################################################################################
env > /tmp/env.txt
#PARAMTERS FROM APP DIRECTOR SCAN_PORT="$scan_port" DB_NAME="$db_name" ORACLE_USER_PASSWORD="$oracle_user_password" SELF_IP="$self_ip"
#FUNCTION TO DISPLAY ERROR AND EXIT function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
echo "Paramter Validation" if [ "x${SCAN_PORT}" = "x" ]; then error_exit "SCAN_PORT not set." fi
if [ "x${DB_NAME}" = "x" ]; then error_exit "DB_NAME not set." fi
if [ "x${ORACLE_USER_PASSWORD}" = "x" ]; then error_exit "ORACLE_USER_PASSWORD not set." fi
# Stop the application server. This parameter should point to the script used to stop the application server. $service_stop
# Ensure that webapps_dir property is assigned to the application server webapps_dir property. cp $war_file $webapps_dir
# Start the application server. This parameter should point to the script used to start the application server. $service_start
#creating txt file to read host, port and DB name cd /tmp echo $SELF_IP"#TEST#"$SCAN_PORT"#TEST#"$DB_NAME >> ip.txt check_error "FAILED TO CREATE ip.txt FILE.";
echo $ORACLE_USER_PASSWORD >> pwd.txt check_error "FAILED TO CREATE pwd.txt FILE.";
remotehost_ip=${remote_ip[0]} echo $remotehost_ip"#TEST#"$SCAN_PORT"#TEST#"$DB_NAME >> ip_other.txt check_error "FAILED TO CREATE ip_other.txt FILE.";
echo -e "\n" echo "URL TO ACCESS THE SAMPLE APPLICATION ACCESS:" echo $SELF_IP":8080/OracleDemoApp/Login.jsp" echo "TO LOGIN USE, USERNAME:admin PASSWORD:admin"
