supported-os-details.exec-script:
import org.apache.commons.lang.StringUtils;
import net.sf.json.JSONArray;
import net.sf.json.JSONObject;
import net.sf.json.JSONSerializer;
import net.sf.json.util.JSONUtils;
import org.apache.commons.io.FileUtils;
import java.io.PrintStream;

//overriding print method as beanshell default print() prints to console
print ( String arg ) {
	System.out.println(arg);
}

try {
	//get the command line arguments and assign to the variables
	if(bsh.args.length < 3) {
		throw new Exception("Insufficient arguments, all 3 arguments are required for the script to execute viz. wrapper.bsh <script_file> <json_input_file> <output_properties_file>");
	}
	script_file=bsh.args[0];
	json_input_properties=bsh.args[1];
	output_properties_file=bsh.args[2];
	//check if the 4th argument is provided, it will be output log file, set out and error stream to the file
	if(bsh.args.length == 4) {
		File outputLogFile = new File(bsh.args[3]);
		FileUtils.touch(outputLogFile);
		PrintStream logStream = new PrintStream(outputLogFile);
		System.setOut(logStream);
		System.setErr(logStream);
	}

	print ("processing input properties");
	File file = new File(json_input_properties);
	StringBuilder fileContents = new StringBuilder((int)file.length());
	Scanner scanner = new Scanner(file);
	String lineSeparator = System.getProperty("line.separator");
	inputProperties="";
	try {
	    while(scanner.hasNextLine()) {        
	        fileContents.append(scanner.nextLine() + lineSeparator);
	    }
	    inputProperties=fileContents.toString();
	} finally {
	    scanner.close();
	}
	propNameList=new ArrayList();
	if(!StringUtils.isBlank(inputProperties)) {
		jsonObject = (JSONObject)JSONSerializer.toJSON(inputProperties);
		jsonIterator=jsonObject.keys();
		while(jsonIterator.hasNext()) {
		    key=jsonIterator.next();
		    if(JSONUtils.isArray(jsonObject.get(key))) {
		    	//array properties are converted to Java Arrays
		        this.interpreter.set(key, JSONArray.fromObject(jsonObject.get(key)).toArray());
		    } else {
		        this.interpreter.set(key,jsonObject.get(key));
		    }
		    propNameList.add(key);
		}
	} else {
		print("input properties file doesn't have any property");
	}

	//execute the beanshell script
	print("executing the script: " + script_file);
	source(script_file);
	print("creating output properties: " + output_properties_file);
	StringBuilder outputPropContents = new StringBuilder();
	for(String propKey : propNameList) {
		value=this.interpreter.get(propKey);
		//currently appd will only support string output properties
		if(value instanceof String) {
			outputPropContents.append(propKey+"="+value+"\n");
		}
	}
	FileUtils.writeStringToFile(new File(output_properties_file), outputPropContents.toString());
	//exit normally with exit code as zero
	System.exit(0);
} catch(ex) {
	print("Error while executing the beanshell script: " + ex.getMessage());
	ex.printStackTrace();
	//exit with the non-zero exit code
	System.exit(1);
}

