ns9:component-version-script.script:
#!/bin/bash

export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin
set -e

# import global configuration data from ApplicationDirector
. $global_conf

################## INTERNAL PARAMETERS #########################
DOWNLOAD_URL="$download_url"
INSTALL_DIR="$install_dir"

###########INTERNAL FUNCTIONS################
# FUNTION TO CHECK ERROR
function check_response_code()
{
   if [ "$?" = "0" ]; then
      echo "Database Installed Successfully";
   else
      error_exit "Unsuccessful Installation. Error Code $?";
   fi
}

function check_error()
{
   if [ ! "$?" = "0" ]; then
      error_exit "$1";
   fi
}

# FUNCTION TO DISPLAY ERROR AND EXIT
function error_exit()
{
   echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2
   exit 1
}

# FUNCTION TO VALIDATE THE INTEGER
function valid_int()
{
   local  data=$1
   if [[ $data =~ ^[0-9]{1,9}$ ]]; then
      return 0;
   else
      return 1
   fi
}

# FUNCTION TO VALIDATE NAME STRING
function valid_string()
{
    local  data=$1
    if [[ $data =~ ^[A-Za-z]{1,}[A-Za-z0-9_-]{1,}$ ]]; then
       return 0;
    else
       return 1;
    fi
}

# FUNCTION TO VALIDATE PASSWORD
function valid_password()
{
    local  data=$1
    length=${#data}
    if [ $length -le 6 ]; then
        check_error "PASSWORD MUST BE OF AT LEAST 6 CHARACTERS"
    else
        if [[ $data =~ ^[A-Za-z]{1,}[0-9_@$%^+=]{0,}[A-Za-z0-9]{0,}$ ]]; then
           return 0;
        else
           return 1;
        fi
    fi
}

function check_install_type()
{
    len=${#INSTALL_EDITION_ARRAY[*]}
    local temp=$1;
    for (( i=0; $i < $len; i++ )) do
       if [ "${INSTALL_EDITION_ARRAY[$i]}" = "$temp" ]; then
          return;
       fi
    done
    error_exit "INVALID INSTALLATION TYPE. Valid values are - WORKGROUP_SERVER_EDITION ENTERPRISE_SERVER_EDITION EXPRESS_EDITION PERSONAL_EDITION"
}

##################### PARAMETER VALIDATION #########################################
echo "VALIDATING PARAMETERS..."
if [ "x${DOWNLOAD_URL}" = "x" ]; then
    error_exit "DOWNLOAD URL NOT SET."
fi

if [ "x${INSTALL_DIR}" = "x" ]; then
    error_exit "INSTALL DIR NOT SET."
fi

################INSTALLING WSO2##############################
echo "Installing Pre-requistes"
if [ -f /etc/redhat-release ] ; then
   echo "RHEL / CENTOS OS"
   yum --nogpgcheck -y install unzip
elif [ -f /etc/debian_version ] ; then
   echo "Ubuntu OS"   
   apt-get -f -y install linux-firmware --fix-missing
   apt-get -f -y install unzip
elif [ -f /etc/SuSE-release ] ; then
   echo "SUSE OS"
   zypper --non-interactive --no-gpg-checks install unzip
fi



mkdir $INSTALL_DIR
cd $INSTALL_DIR
### Downloading tar ball
echo "Downloading the tar ball"
wget --output-document=installer.zip $DOWNLOAD_URL
echo "Downloading the tar ball - DONE"

### Extracting tar ball
echo "Extracting the tar ball"
unzip installer.zip
echo "Extracting the tar ball - DONE"

